package Array;

public class Sum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a[] = {1,2,3,4,5};

        int sum = 0; 

  
        for (int i = 0; i < 5; i++) {
            sum += a[i]; 
        }

     
        System.out.println("Sum is: " + sum);
	}

}
