package ConditionalLoop;

public class NestedFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

       int a=1;
		
		for(int i=1; i<=1; i++){ //outer row
			

			for(int j=a; j<=i; j++){ //inner column
				//System.out.print( " i= "+i+"  j= " +j);
				System.out.print(i);
	         	}
			
			System.out.println( );
         	}
	}

}
